sources.css/style.scss:
@import url('owl.carousel/dist/assets/owl.carousel.css');
@import url('bootstrap/dist/css/bootstrap.min.css');
@import url('aos/dist/aos.css');
// @import url('https://use.typekit.net/ngn2ooc.css');
@import 'predefinied.scss';
@import 'pageTrans.scss';

.stop-scrolling {
    height: 100vh;
    overflow: hidden;
}

html,
body {
    margin: 0;
    padding: 0;
    -webkit-user-drag: none;
    -moz-user-drag: none;
    -ms-user-drag: none;
    -webkit-user-modify: none;
    -moz-user-modify: none;
    -ms-user-modify: none;
    -webkit-tap-highlight-color: transparent;
    -webkit-font-smoothing: antialiased;
    // user-modify: none;
    // tap-highlight-color: transparent;
    // font-smoothing: antialiased;
    font-family: $fontStack;
    font-weight: 300;
}

h1,
h2,
h3,
h4,
h5,
h6,
p,
label,
input,
button,
a,
span {
    font-weight: $fontWeightThin !important;

    &::selection {
        background: $clern-blue;
        color: white;
    }
}


.header {
    position: fixed;
    z-index: 1024;
    width: 100%;
    display: flex;
    top: 0;
    transition: 0.5s;

    .container-fluid {
        height: 72px;
        position: relative;
        background: transparent;
        border-radius: .0010px;
        transition: $durationM;

        &.scrolled {
            box-shadow: 0 20px 35px rgba(0, 0, 0, 0.2);
            margin-top: 0.75rem;
            border-radius: 200px;
            background: linear-gradient(-90deg, $clern-blue 50%, white 50%);
            padding: 0 0.4rem;


            .header {
                color: white;

                &__logo-img {
                    max-height: 32px;
                    margin-left: 1.5rem;
                }

                &__lang-container {
                    height: 72px;
                    width: 72px;
                    box-shadow: none;
                }

                &__lang-all {
                    top: calc(100% - 72px);
                    width: 72px;
                }
            }
        }



    }


    &__logo {
        display: flex;
        position: relative;
        z-index: 16;
        transition: 0.5s $bezierFastoutSlowin;
        cursor: pointer;
        color: $clern-darkGrey;
        line-height: 2.5rem;
        font-size: $fontSizeBodyL;

        &:hover {
            transform: scale(1.1);
        }

        &:active {
            transform: scale(1);
        }

        &-img {
            max-height: 48px;
        }

        svg {
            width: 2.5rem;
            height: 2.5rem;
            padding: 0.25rem 0;
            margin-right: 1rem;

            path,
            rect {
                fill: white;
            }
        }
    }

    .nav-toggle {
        // position: fixed;
        top: 1rem;
        right: 2.5rem;
        margin-right: 1rem;
        z-index: 1024;
        display: none;
        background: none;
        border: 0;
        outline: 0;
        height: 48px;
        align-items: center;

        svg {
            color: $clern-darkGrey;
            transition: $bezierFastoutSlowin 0.5s;
            top: 50%;
            left: 50%;
            position: absolute;
            width: 28px;
            height: 28px;

            path {
                fill: white;
                transition: $bezierFastoutSlowin 0.5s;
            }
        }

        &__inner {
            position: relative;
        }

        &__icon--menu {
            opacity: 1;
            transform: translate(-50%, -50%);
        }

        &__icon--close {
            opacity: 0;
            transform: translate(-50%, -50%) rotate(45deg);
        }
    }


    .button-icon-only {
        align-items: center;
        justify-content: center;
        padding: 0;
    }

    &__nav {
        flex-grow: 10;
        display: flex;
        align-items: center;
        margin-left: calc(-207px / 2);
    }

    &__nav-list {
        display: flex;
    }

    &__nav-right .header__nav-link {
        color: white;
        display: inline-block;
        position: relative;

        &::before {
            content: '';
            width: 0px;
            height: 2px;
            background-color: white;
            bottom: -0.5rem;
            position: absolute;
            left: 0;
            transition: $durationS $bezierFastoutSlowin;
        }

        &:hover {
            color: white;

            &:before {
                width: 100%;
            }
        }
    }

    &__nav-link {
        color: black;
        align-self: center;
        font-weight: $fontWeightMedium;
        font-size: $fontSizeBodyL;
        text-decoration: none;
        position: relative;
        transition: color $durationS ease .1s;
        line-height: 1;
        padding: 0 $spaceS;
        margin: 0 $spaceS;
        border-radius: 0;
        letter-spacing: $spaceLetters;
        transition: $bezierFastoutSlowin 0.36s;
        font-weight: bold;

        &:active,
        &:hover {
            text-decoration: none;
            color: $clern-blue;

            &:before {
                width: 100%;
            }
        }

        &.active {
            color: white;
        }
    }

    &__lang-container {
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        display: flex;
        align-items: center;
        align-self: center;
        justify-content: center;
        height: 60px;
        width: 60px;
        background: white;
        box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
        border: none;
        border-radius: 50%;
        transition: $durationM;

        svg polygon {
            transition: $durationM;
        }

        &:hover,
        &:focus,
        &:active {
            outline: none;
            border: none;
            background: $clern-blue;

            // svg path,
            // svg polygon {
            //     fill: white;
            // }

            .header__lang-all {
                opacity: 1;
                transform: translateY(0);
            }
        }
    }

    &__lang-active {
        display: -webkit-box;
        z-index: 2;
        position: relative;
    }

    &__lang-arrow {
        width: 10px;
        margin-left: 4px;
    }

    &__lang-all {
        position: absolute;
        top: calc(100% - 60px);
        transform: translateY(-50%);
        background: white;
        border: 1px solid $clern-blue;
        padding: 70px 0 20px 0;
        width: 60px;
        border-radius: 200px;
        z-index: -1;
        color: black;
        transition: $durationL $bezierFastoutSlowin;
        // transition-delay: 0.5s, 0s;
        // transition-property: opacity, transform;
        font-size: $fontSizeBodyXS;
        opacity: 0;

        input[type=radio] {
            display: none;
        }

        .text {
            cursor: pointer;
            margin: auto;
        }

        input[type="radio"]:checked~.text {
            color: $clern-blue;
            font-weight: 500;
        }
    }

    &__btn {
        background-color: white;
        color: $clern-blue;
        border: 1px solid white;
        width: 130px;
        height: 60px;
        padding: 0;
        margin-left: 1rem;
        line-height: 60px;
        text-transform: uppercase;
        transition: $durationM;

        &:hover {
            background-color: $clern-blue;
            color: white;
        }

        &:active {
            transform: scale(0.95);
        }
    }
}


.owl-main {
    &__item {
        display: flex;
        align-items: center;
    }

    &__left {
        margin-top: 88px;
    }

    &__heading {
        color: $clern-lightGreen;
        font-size: 5.625rem;
        position: relative;
        transform: translate3d(0px, 0px, 0px) rotate(0.0001deg);
        transform-style: preserve-3d;

        .letter {
            display: inline-block;
            transform: translate3d(0px, 0px, 0px) rotate(0.0001deg);
            transform-style: preserve-3d;
        }


        &::before {
            content: '';
            background-color: #d3ef8b;
            height: 10px;
            width: 70%;
            position: absolute;
            display: block;
            left: 0;
            bottom: 15px;
            z-index: -1;
            border-radius: 200px;

        }
    }

    &__p {
        line-height: 1.6;
        font-size: $fontSizeBodyL;
    }


    &__text {
        max-width: 750px;
        margin: auto;
        transform: translate3d(0px, 0px, 0px) rotate(0.0001deg);
        transform-style: preserve-3d;
    }

    &__btn {
        text-transform: uppercase;
        border-radius: 200px;
        background-color: white;
        border: none;
        transition: $durationM;
        font-size: $fontSizeBodyL;
        color: white;
        position: relative;
        box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
        overflow: hidden;
        transform: translate3d(0px, 0px, 0px) rotate(0.0001deg) translateX(0);
        transform-style: preserve-3d;

        &:before {
            content: '';
            background: $clern-blue-gradient;
            width: calc(100% + 2px);
            height: calc(100% + 2px);
            z-index: -1;
            transition: $durationL $bezierFastoutSlowin;
            position: absolute;
            left: -1px;
            top: -1px;
            border-radius: 200px;
        }

        &:hover {
            color: $clern-blue;
            text-decoration: none;
            transform: translate3d(0px, 0px, 0px) rotate(0.0001deg) scale(1.05);

            &:before {
                transform: translate3d(0px, 0px, 0px) rotate(0.0001deg) translateX(-95%);
                // transform: rotate(180deg) translate(-240px, 0px);
            }
        }

        &:active {
            color: $clern-blue;
            outline: none;
            box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
            transform: scale(0.95);
        }


        &:focus {
            outline: none;
            box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
        }
    }

    &__right {
        position: absolute;
        top: 0;
        right: 0;
    }

    &__img-container {
        max-width: 100%;
        max-height: 100%;
        position: relative;
        text-align: right;
        overflow: hidden;
        transform: translateX(calc(1rem + 0.4px));
    }

    &__border-dots {
        // border-image-source: url("../images/svg/dots.svg");
        border-image-slice: 33% 33%;
        border-style: dotted;
        border-color: white;
        border-width: 4.8px;
        border-image-repeat: round;
        position: absolute;
        top: 40%;
        left: 26%;
        border-top-left-radius: 50px;
        width: 30%;
        height: 500%;
        border-right: none;
        border-bottom: none;

        &.second {
            width: 17.9%;
        }

        &.third {
            width: 19%;
        }
    }


    &__img {
        object-fit: cover;
        max-width: 100%;
        width: 88% !important;
        display: inline-block !important;
        z-index: -1;
        // transform: translateX(calc(1rem + 1px));
        // transform: translate(25rem, -18.15rem);
        // opacity: 0.4;
        // animation: mainOwlImgAnimation 1s forwards;
    }

    .owl-dots {
        position: absolute;
        top: 50%;
        right: 2vw;
        transform-origin: bottom;
        transform: translate(-50%, -50%) rotate(90deg);
    }

    .main-counter {
        position: absolute;
        right: calc(1rem + 1px + 7vw);
        top: calc(50% + 85px);
        color: white;
        font-size: $fontSizeBodyL;

        &__number {
            font-size: $fontSizeH2;
            margin-right: 5px;
            font-weight: 400;
        }
    }

    .owl-dots .active span {
        transform: translate(-2px, -13px) scale(1.5);
        margin-right: 6px;
    }

    .owl-dots button {
        outline: none;
    }

    .owl-dots .owl-dot span {
        height: 2px;
        width: 2px;
        box-shadow: inset white 0 0 0px 2px;
        box-sizing: border-box;
        background-color: transparent !important;
        border-radius: 50%;
        padding: 5px;
        margin: 10px 5px;
        float: left;
        outline: none;

    }

    .bar {
        width: 0%;
        height: 100%;
        background: #fff;
        opacity: 1 !important;
        z-index: 3000;
        position: relative;
    }

    .progressBar {
        display: inline-block;
        width: 4rem;
        height: 2px;
        position: relative;
        background: #94d08b;
        z-index: 3000;
        transform: translateY(-14px);
    }

    .line-container {
        display: flex;
        justify-content: center;
    }
}


.steps {
    width: 100%;
    height: 100%;
    position: relative;
    overflow: hidden;
    background-image: url('../images/background-steps-with-tel.png');
    background-size: cover;
    background-repeat: no-repeat;
    background-color: #fff;
    background-position-x: -22vmin;
    background-position-y: -30px;
    padding: 3rem 0 17vw 0;

    &__border-dots {
        // border-image-source: url("../images/svg/dots.svg");
        border-image-slice: 33% 33%;
        border-style: dotted;
        border-color: $clern-blue;
        border-width: 4.8px;
        border-image-repeat: round;
        position: absolute;
        top: -32.2%;
        left: 43.25%;
        border-bottom-right-radius: 50px;
        width: 15%;
        height: 50%;
        border-top: none;
        border-left: none;
        z-index: 0;

        &.only-height {
            border-bottom: none;
            height: 10%;
            border-radius: 0;
            top: -15%;
            left: 50%;
            transform: translateX(-50%);
            width: 5px;
        }

        &.svg {
            border-radius: 0;
            border-right: none;
            top: 50%;
            transform: translateY(-50%);
            left: calc(100% - 140%/2 + 120%);
            width: 140%;
            height: 5px;
        }
    }

    &__heading {
        color: $clern-blue;
        font-size: $fontSizeH1;
    }

    &__subheading {
        font-size: $fontSizeBodyL;
        line-height: 1.364;
    }

    &__intro {
        padding-top: 18vh;
        padding-bottom: 80px;
    }

    &__svg {
        background: white;
        box-shadow: 0px 7px 18px 0px rgba(187, 193, 194, 0.3);
        border-radius: 15px;
        max-height: 85px;
        max-width: 85px;
        position: relative;
        text-align: center;

        img {
            margin: 6px;
        }
    }

    &__svg-1::after,
    &__svg-2::after,
    &__svg-3::after {
        content: '01';
        color: $clern-blue;
        position: absolute;
        width: 10px;
        font-size: 1rem;
        right: -12px;
        height: 10px;
        bottom: 8px;
    }

    &__svg-2::after {
        content: '02';
    }

    &__svg-3::after {
        content: '03';
    }

    &__info-step-heading {
        color: $clern-blue;
        font-size: $fontSizeH3;
    }

    &__info-step-subheading {
        font-size: 1rem;
    }
}

.offer {
    color: $clern-blue;
    transition: $durationM;
    margin-top: calc(-18vw + 150px);
    z-index: 10;
    position: relative;

    &__heading {
        color: $clern-lightGreen;
        font-size: $fontSizeH1;
        text-align: center;
    }

    .owl-offer,
    .owl-stage-outer,
    .owl-stage {
        min-height: 450px;
    }

    .owl-item {
        transform: translateY(37px);
        padding: 0.5rem;
        border-bottom-right-radius: 25px;
        border-top-left-radius: 25px;
        transform: translateY(37px);
        transition: $durationL;
        height: 320px;

        &:hover {
            transition: $durationL;
            transform: translateY(10px);
            height: 347px;

            .offer {
                &__item {
                    background: $clern-blue;
                    cursor: pointer;

                    &::after {
                        opacity: 0.9;
                    }
                }

                &__line {
                    fill: #ffffff !important;
                    stroke: #ffffff !important;
                }

                &__svg {
                    opacity: 0;
                }

                &__more {
                    border-radius: 25px;
                    width: 55%;
                    background-color: white;
                }

                a {
                    text-decoration: none;
                }

                &__item-heading {
                    color: white;
                }

                &__more-dots {
                    right: 5px;
                    left: initial;
                    color: $clern-blue;
                }

                &__more-hidden {
                    left: 38%;
                    z-index: 2;
                    display: inline-block;
                }
            }
        }


    }

    &__item {
        box-shadow: rgba(25, 25, 25, 0.1) -1px -1px 10px 3px;
        border-bottom-right-radius: 25px;
        border-top-left-radius: 25px;
        position: relative;
        transition: $durationL, border-radius 0s;
        z-index: 1;
        background-color: white;
        height: 100%;
        display: block;
    }

    &__item::after {
        content: "";
        opacity: 0;
        transition: $durationL;
        background: url('../images/offer-flats-bg.png');
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        z-index: 0;
        position: absolute;
        background-size: cover;
        background-repeat: no-repeat;
        border-bottom-right-radius: 25px;
        border-top-left-radius: 25px;
        z-index: 1;
    }

    &__svg {
        max-height: 60px;
        transition: 1s;
        opacity: 1;
    }

    &__svg path {
        text-align: start;
        fill: $clern-blue !important;
    }

    &__more {
        display: inline-block;
        position: absolute;
        font-size: 1.5rem;
        background-color: $clern-blue;
        padding: 1rem;
        border-radius: 50%;
        bottom: 1rem;
        right: 1rem;
        transition: 0.5s all;
        width: 45px;
        height: 45px;
        box-shadow: rgba(25, 25, 25, 0.1) -1px -1px 5px 3px;
        z-index: 2;
        letter-spacing: 0.45px;
    }

    &__more-hidden {
        display: none;
        position: absolute;
        text-transform: uppercase;
        top: 50%;
        left: -25px;
        color: $clern-blue;
        font-size: $fontSizeBodyS;
        transform: translate(-50%, -50%);
    }

    &__more-dots {
        position: absolute;
        color: white;
        font-size: $fontSizeBodyL;
        letter-spacing: 1px;
        transform: translate(-50%, -50%);
        top: 50%;
        left: 50%;
        font-family: auto;
    }


    &__item-heading {
        font-size: $fontSizeBodyL;
        position: relative;
        display: block;
        z-index: 2;
        height: 4rem;
        color: $clern-blue;
        text-decoration: none;
    }


    &__line {
        position: relative;
        width: 20%;
        display: block;
        fill: $clern-blue;
        stroke: $clern-blue;
        z-index: 2;
    }

    a:hover {
        text-decoration: none;
    }
}


.testimonials {
    position: relative;

    &--main-site {
        margin-top: calc(-14vh - 140px)
    }

    &__heading {
        font-size: $fontSizeH1;
    }

    .container-fluid {
        background: #5db65a;
        background: linear-gradient(55deg, rgb(92, 183, 90) 0%, rgb(147, 208, 86) 99%);
        padding-top: calc(10vh + 347px/2);
        min-height: 40vh;
        border-bottom-right-radius: 10vh;
        border-top-left-radius: 10vh;
        width: 90%;
    }

    &--about-us .container-fluid {
        padding-top: 110px;
    }

    &__item {
        max-width: 969px;
        margin: 0 auto;
    }

    .no-radius {
        border-radius: 0 !important;
    }

    &__portrait,
    .owl-carousel .owl-item img {
        border-radius: 50%;
        background-color: white;
        width: 90px;
        display: inline-block;
    }

    .owl-dots {
        margin-top: -6vh;
        padding: 3rem 0;
    }

    &__card {
        border: 0;
        background-color: transparent;
    }

    &__blockquote {
        font-size: $fontSizeBodyL;
        line-height: 1.364;
        padding-top: 2rem;
    }

    &__blockquote::before {
        content: ' ';
        position: absolute;
        background-image: url("../images/quotesign.png");
        background-repeat: no-repeat;
        background-size: contain;
        width: 0.75rem;
        height: 0.75rem;
        top: 1rem;
        left: 3rem;
        transform: matrix(3.58676812988438, 0, 0, 3.58643712507357, 0, 0);
    }

    .owl-nav span {
        position: absolute;
        max-height: 70px;
        top: 0;
        transition: $durationM;
        color: white;
        background-repeat: no-repeat;
        background-size: contain;
        background-position: center;
        width: 3rem;
        height: 70px;
        color: transparent;

        &:hover {
            transform: translate(10px, 10px);
        }

        &:active {
            transform: scale(0.8) translate(10px, 10px);
        }
    }

    .owl-prev span {
        left: 5%;
        background-image: url("../images/svg/light-arrow-left.svg");

    }

    .owl-next span {
        background-image: url("../images/svg/light-arrow-right.svg");
        right: 5%;

        &:hover {
            transform: translate(-10px, 10px);
        }

        &:active {
            transform: scale(0.8) translate(-10px, 10px);
        }
    }
}


.news {
    margin: 0 auto;
    margin-top: 100px;

    &__heading {
        color: $clern-blue;
        font-size: $fontSizeH2;
    }

    &__subheading {
        line-height: 1.8;
        font-size: $fontSizeBodyS;
    }

    &__more {
        display: inline-block;
        position: relative;
        background: white;
        font-size: 1.5rem;
        transition: 0.5s, color 0.2s;
        box-shadow: rgba(20, 184, 235, 0.5) 0px 5px 12px 0px;
        z-index: 2;
        border-radius: 25px;
        width: 128px;
        cursor: pointer;
        color: $clern-blue;
        overflow: hidden;

        &::before {
            content: '';
            position: absolute;
            left: -100%;
            top: 0;
            height: 100%;
            width: 100%;
            border-radius: 25px;
            background: rgba(0, 172, 224, 1);
            transition: $durationM;
            background: linear-gradient(45deg, rgba(0, 172, 224, 1) 0%, rgba(117, 223, 255, 1) 100%);
        }
    }

    &__more:hover {
        color: white;

        &::before {
            left: 0;
        }
    }

    &__more-hidden {
        display: none;
        position: absolute;
        text-transform: uppercase;
        top: 50%;
        left: -25px;
        font-size: $fontSizeBodyS;
        transform: translate(-50%, -50%);
        left: 38%;
        z-index: 2;
        display: inline-block;
    }

    &__more-dots {
        position: absolute;
        font-size: 2rem;
        transform: translate(-50%, -71%);
        right: 5px;
    }


    &__img-description {
        font-size: $fontSizeBodyS;
        color: $clern-darkGrey;
    }

    &__item a:hover {
        text-decoration: none;
        cursor: pointer;
    }

    &__time {
        color: $clern-blue;
        font-size: $fontSizeBodyXS;
    }

    &__img {
        border-bottom-right-radius: 35px;
        border-top-left-radius: 35px;
        display: inline-block;
        border-bottom-right-radius: 35px;
        border-top-left-radius: 35px;
        position: relative;
        width: 100%;
        height: 100%;
    }

    &__img-container {
        position: relative;
        width: 100%;
        height: 100%;
        z-index: 1;
        border-bottom-right-radius: 35px;
        border-top-left-radius: 35px;
        overflow: hidden;
    }

    &__img-container::before {
        content: '• • •';
        width: 40px;
        height: 40px;
        border-radius: 50%;
        border: 2px solid white;
        position: absolute;
        color: white;
        bottom: -100%;
        left: -100%;
        transform: translate(-50%, 50%);
        display: block;
        text-align: center;
        line-height: 36px;
        letter-spacing: -0.1px;
        z-index: 20;
        opacity: 0;
        transition: $durationM;
        font-size: $fontSizeBodyXS;
    }

    &__item:hover &__img-container::before {
        opacity: 1;
        bottom: 50%;
        left: 50%;
    }

    &__img-container::after {
        content: '';
        display: block;
        background-color: $clern-blue;
        width: 100%;
        height: 100%;
        opacity: 0;
        top: -100%;
        right: -100%;
        position: absolute;
        background-size: cover;
        background-repeat: no-repeat;
        border-bottom-right-radius: 35px;
        border-top-left-radius: 35px;
        transition: $durationM;
        z-index: 2;
    }

    &__item:hover &__img-container::after {
        opacity: 0.7;
        top: 0;
        right: 0;
    }

}


.newsletter {
    margin: 0 auto;

    &__heading {
        font-size: $fontSizeH1;
    }

    .row {
        background-color: $clern-blue;
        border-radius: 15vh;
        border-bottom-right-radius: 0;
        position: relative;
    }

    &__input-container {
        display: flex;
        align-items: center;
    }


    &__btn {
        border-radius: 35px;
        border-bottom-right-radius: 0;
        background-color: white;
        color: $clern-blue;
        border: 3px solid transparent;
        transition: $durationM;
        box-shadow: rgba(25, 25, 25, 0.1) -1px -1px 5px 3px;
        font-size: $fontSizeBodyS;
        text-transform: uppercase;
    }

    &__btn:hover {
        background-color: transparent;
        color: white;
        border: 3px solid white;
        border-radius: 35px;
        border-bottom-right-radius: 35px;
        border-top-right-radius: 0;
    }

    .form-label {
        font-size: $fontSizeBodyS;
        color: white;
        left: 1rem;
    }

    .form-input {
        border-bottom: 1px solid rgba(255, 255, 255, 0.5);
        color: white;
        border-left: none;

        &:focus {
            border-bottom: 1px solid rgba(255, 255, 255, 1);
        }
    }
}


@media (min-width: 1201px) and (max-width:1440px) {
    .owl-main {
        &__heading {
            font-size: 5rem;
        }

        &__btn {
            margin-top: 1.5rem !important
        }
    }

    .steps {
        &__border-dots.only-height {
            display: none;
        }
    }
}

@media (min-width: 1201px) and (max-width:1439px) and (orientation: landscape) {
    .steps {
        &__intro {
            padding-left: 11rem;
            padding-top: 23vh;
            padding-bottom: 16px;
        }

        &__border-dots.border-bottom-right {
            width: 10%;
            left: 48.6%;
        }
    }
}

@media (max-width: 1200px) {
    .header {
        bottom: initial;
        top: 0;
        left: 1rem;
        width: calc(100% - 2rem);
        padding: 0;
        height: 48px;

        .container-fluid {
            margin-top: 0.75rem;
            min-height: initial;

            &.scrolled {
                background: white;

                .header__lang-all {
                    top: initial;
                    width: auto;

                    .container {
                        margin: 0;
                    }
                }
            }
        }


        &__logo {
            padding: 0;

            &-img {
                max-height: 32px;
            }
        }

        &__nav {
            position: fixed;
            top: 0;
            right: 0;
            bottom: 0;
            left: 0;
            background: rgba(255, 255, 255, 0.8);
            transform: translate3d(0, -100%, 0);
            transition-duration: $durationL;
            transition-timing-function: $bezierFastoutSlowin;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            backdrop-filter: blur($spaceS);
            max-width: 100%;
            width: 100%;
            opacity: 0;
            margin: 0;


            &--entered {
                transform: translateZ(0);
                opacity: 1;
            }

            &-right {
                text-align: center;
            }

        }

        &__nav-list {
            transform: none;
            display: block;
            flex-direction: initial;
        }

        &__nav-link {
            width: 100%;
            display: block;
            font-size: $fontSizeBodyL;
            padding: 16px;
            text-align: center;
            color: black !important;
        }

        .nav-toggle {
            display: flex;
            background: $clern-lightGreen;
            margin-right: 0;
            width: 48px;
            height: 48px;
            border-radius: 50%;

            &__inner {
                width: 32px;
                height: 32px;
            }

            &--entered {
                .nav-toggle__icon--menu {
                    opacity: 0;
                    transform: translate(-10%, -50%);
                }

                .nav-toggle__icon--close {
                    opacity: 1;
                    transform: translate(-50%, -50%) rotate(0);
                }
            }
        }

        &__lang-active {
            position: relative;
            z-index: 10;
        }

        &__lang-active-text {
            color: $clern-lightGreen;
        }

        &__lang-arrow {
            display: none;
        }

        &__lang-container {
            bottom: 1.5rem;
            left: 50%;
            transform: translateX(-50%);
            color: black;
            background: transparent;
            box-shadow: none;
            width: auto;
            margin-top: 1.5rem;
            display: flex;
            width: 100%;
            justify-content: space-around;
            font-size: 1rem;


            &:hover,
            &:focus,
            &:active {
                background: transparent;
                box-shadow: none;
                border: none;
            }
        }

        &__lang-all {
            display: flex;
            position: initial;
            background: none;
            padding: 0.25rem 1rem;
            width: auto;
            color: black;
            transform: none;
            opacity: 1;
            z-index: 1;
            font-size: 1rem;

            .container {
                margin: 0;
                padding: 0.5rem 1rem;
            }
        }

        &__lang-active {
            width: 72px;
            height: 72px;
            border: 1px solid green;
            border-radius: 50%;
            margin-right: 0.5rem;
            display: -webkit-box;
            align-items: center;
            justify-content: center;
            display: none;
        }

        &__btn {
            margin-top: 0.5rem;
            color: black;
            border-color: black;
            background: transparent;
            height: 50px;
            line-height: 50px;
        }
    }

    .owl-main {
        &__heading {
            font-size: $fontSizeH1;

            &::before {
                bottom: 5px;
            }
        }

        &__left {
            margin-top: 10vh;
            padding-bottom: 2rem;
        }

        .owl-dots {
            top: 135px;
            right: -3.5rem;
        }

        .main-counter {
            right: 3.5rem;
            top: calc(50% + 3rem);
        }

        &__p {
            max-width: 40vw;
        }
    }

    .steps {
        background-position-x: -300px;
        display: block;
        position: relative;

        &__intro {
            padding-top: 10vh;
        }

        &__border-dots {
            top: -39.2%;
            left: 33.8%;
            width: 25%;

            &.only-height {
                top: -15%;
                left: 50%;
                width: 5px;
            }

            &.svg {
                top: 50%;
                width: 80%;
                left: calc(100% - 140%/2 + 120%);
            }
        }
    }

    .steps::before {
        content: '';
        background: white;
        display: block;
        opacity: 0.8;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        z-index: 0;
        position: absolute;
        z-index: 0;
    }


    .testimonials {

        .container-fluid {
            width: 100%;
        }

    }
}

@media (min-width: 993px) and (max-width:1200px) and (orientation: portrait) {
    .owl-main {
        &__left {
            margin-top: 20vh;
        }

        &__heading {
            margin-top: -10vh;
        }



        &__btn {
            margin-bottom: 0;
            z-index: 3;
        }
    }

    .steps {
        margin-top: -10vh;

        &__border-dots.only-height {
            top: calc(-15% - 20vh);
            height: 20vh;
        }
    }
}

@media (max-width: 992px) {


    .owl-main {
        .main-counter {
            display: none;
        }

        &__border-dots {
            display: none;
        }
    }

    .steps {
        &__intro {
            padding: 12vh 0 50px 0;
        }

        &__border-dots {
            display: none;
        }
    }

    .offer>.container {
        margin: 0;
        width: 100%;
        max-width: 100%;
    }

    .newsletter .row {
        border-radius: 0;
    }

}

@media (max-width: 480px) {
    .header .container-fluid {
        padding: 0;
    }

    .owl-main {
        &__heading {
            font-size: 13vw;
        }

        &__left {
            margin-top: 21vh;
        }

        &__p {
            max-width: initial;
        }


    }
}

@media (min-width: 1921px) {
    .steps {
        &__border-dots {
            &.border-bottom-right {
                left: 42.6%;
            }
        }
    }
}
